mapper.select:
SELECT SHOP.ID AS SHOP_ID, SHOP.NAME AS SHOP_NAME, SHOP.DESCRIPTION AS SHOP_DESCRIPTION, SHOP.IMG_URL AS SHOP_IMG_URL, SHOP.OWNER_USER_ID AS SHOP_OWNER_USER_ID, SHOP.CREATED_AT AS SHOP_CREATED_AT, SHOP.UPDATED_AT AS SHOP_UPDATED_AT, GOODS.ID AS GOODS_ID, GOODS.NAME AS GOODS_NAME, GOODS.DESCRIPTION AS GOODS_DESCRIPTION, GOODS.DETAILS AS GOODS_DETAILS, GOODS.IMG_URL AS GOODS_IMG_URL, GOODS.PRICE AS GOODS_PRICE, SHOPPING_CART.NUMBER AS GOODS_NUMBER, GOODS.CREATED_AT AS GOODS_CREATED_AT, GOODS.UPDATED_AT AS GOODS_UPDATED_AT FROM (SELECT DISTINCT SHOP_ID FROM SHOPPING_CART
USER_ID = #{userId}
AND STATUS = #{status}
limit #{offSet},#{limit} ) AS t1 JOIN SHOPPING_CART on t1.SHOP_ID = SHOPPING_CART.SHOP_ID JOIN GOODS on GOODS.ID = SHOPPING_CART.GOODS_ID JOIN SHOP on SHOP.ID = SHOPPING_CART.SHOP_ID WHERE SHOPPING_CART.STATUS = 'ok'
